/* eslint-disable import/prefer-default-export */
// eslint-disable-next-line consistent-return
export const decideDeviationColor = (deviationTime) => {
  const sign = Math.sign(deviationTime);

  if (sign === 0) {
    return 'unset';
  }

  if (sign === 1) {
    return 'green';
  }

  if (sign === -1) {
    return 'red';
  }
};

// Core dependencies
import React from 'react';

// PropTypes
import PaidHoursTypes from './PaidHours.types';

// Global util
import { minToHour } from '../../../../../../utils/utils';

// Local util
import { calcPaidTime } from './utils';

// Style
import { PaidTime } from './style';

const PaidHours = ({ workedTime, deviationTime }) => {
