export default function timesheetStore(
  state = {
    basePlannedTime: 0,
    workedTime: 0,
    dayOffTime: 0,
    defaultStartTime: 0,
    months: getMonths(),
    monthDays: [],
    deviationTime: 0,
    firstDay: 0,
    lastDay: 0,
  },
  action,
) {
  switch (action.type) {
  case 'ALL_DATA_REQUESTED':
    return {
      ...state,
      firstDay: 0,
      lastDay: 0,
      defaultStartTime: 0,
    };
  case 'TIMESHEET_DATA_SUCCEEDED':
    // eslint-disable-next-line no-case-declarations
    const monthDays = formatMonthDays(action.payload.monthDays);
    return {
      ...state,
      basePlannedTime: action.payload.basePlannedTime,
      workedTime: action.payload.workedTime,
      dayOffTime: action.payload.dayOffTime,
      defaultStartTime: action.payload.defaultStartTime,
      monthDays,
      firstDay: action.payload.monthDays[0].date,
      lastDay:
        action.payload.monthDays[action.payload.monthDays.length - 1].date,
      deviationTime: action.payload.deviationTime,
    };
  case 'SET_USERDATA_SUCCEDED':
    return {
      ...state,
      defaultStartTime: action.payload.startTime,
    };
  case 'UPDATE_ROW_TIME_SUCCEEDED':
    return {
      ...state,
      monthDays: updateRowTime(state.monthDays, action.payload),
    };
  case 'UPDATE_ROW_DEVIATION_TYPE':
    return {
      ...state,
      monthDays: updateRowDeviationType(state.monthDays, action.payload),
    };
  case 'UPDATE_USER_STATISTIC':
    return {
      ...state,
      deviationTime: updateDeviationTime(state.deviationTime, action.payload),
    };
  default:
    return state;
  }
}

const officeListTransform = (input) => {
  const target = input;
  let result;
  if (Array.isArray(target)) {
    for (let i = 0; i < target.length; i += 1) {
      result[target[i].short] = target[i].value;
    }
  } else if (target instanceof Object) {
    result = Object.entries(target).map(([key, value]) => ({ short: key, fullname: value }));
  } else {
    throw new Error('officeListTransfrom: wrong input');
  }
  // eslint-disable-next-line no-param-reassign
  return result;
};

export default officeListTransform;

import cloneDeep from 'lodash.clonedeep';
import { isCurrentDay } from '../../utils/utils';

export const formatMonthDays = monthDays => monthDays
  .map(monthDay => ({
    ...monthDay,
    currentDay: isCurrentDay(monthDay.date),
  }));

export const updateDeviationTime = (deviation, { stepDifference }) => deviation + stepDifference;

export const updateRowTime = (monthDays, { type, id, minute }) => {
  const newMonthDays = cloneDeep(monthDays);
  newMonthDays[id][type] = minute;

  return newMonthDays;
};

export const updateRowDeviationType = (monthDays, { id, deviationType }) => {
  const newMonthDays = cloneDeep(monthDays);
  newMonthDays[id].deviationType = deviationType;

  return newMonthDays;
};

