import 'regenerator-runtime/runtime';
import {
  takeEvery, put, call, delay, select,
} from 'redux-saga/effects';
import { getData, postData } from '../services/api';
import { minToHour, stringToPhpTimeStamp, getDeviationTitle } from '../utils/utils';

const baseUrl = 'https://dev.profile.niif.hu/timesheet/react';
console.log('env: ', process.env.URL);

function* getAllData(action) {
  const { year, month } = action.payload;
  let url = `${baseUrl}/fetch`;

  if (year && typeof month === 'number') {
    url = `${baseUrl}/fetch/${year}/${month + 1}`;
  }

  try {
    yield delay(100);
    const data = yield call(getData, url);

    yield put({
      type: 'ALL_DATA_SUCCEEDED',
    });

    yield put({
      type: 'STATIC_DATA_SUCCEEDED',
      payload: data.timesheetData.officeList,
    });

    yield put({
      type: 'USER_DATA_SUCCEEDED',
      payload: data.userData,
    });

    yield put({
      type: 'TIMESHEET_DATA_SUCCEEDED',
      payload: data.timesheetData,
    });
  } catch (error) {
    yield put({
      type: 'ALL_DATA_FAILED',
      payload: error,
    });
    console.log(error); //eslint-disable-line
  }
}

function* postUserData(action) {
  const url = `${baseUrl}/saveTimesheet`;
  try {
    yield delay(100);
    const { office, startTime } = action.payload;
    const postBody = {
      office,
      default_start: minToHour(startTime),
    };

    postData(url, postBody);

    yield put({
      type: 'SET_USERDATA_SUCCEEDED',
      payload: { office, startTime },
    });

    yield put({
      type: 'ALL_DATA_REQUESTED',
      payload: {},
    });
  } catch (error) {
    yield put({
      type: 'SET_USERDATA_FAILED',
      payload: error,
    });
  }
}

function* updateRowTime(action) {
  try {
    const url = `${baseUrl}/ontime`;
    const { type, day, minute } = action.payload;
    const minuteType = type === 'workStart' ? 'start' : 'end';
    // we need to modify the data to match the endpoint criteria
    const postBody = {
      time: minuteType,
      data: {
        day: stringToPhpTimeStamp(day),
      },
    };

    if (minuteType === 'start') {
      postBody.data.time_start = minToHour(minute);
    } else {
      postBody.data.time_end = minToHour(minute);
    }

    yield delay(100);

    postData(url, postBody);

    yield put({
      type: 'UPDATE_ROW_TIME_SUCCEEDED',
      payload: action.payload,
    });
  } catch (error) {
    yield put({
      type: 'UPDATE_ROW_TIME_FAILED',
      payload: error,
    });
  }
}

function* updateUserStatistic(action) {
  try {
    // console.log('REQUEST_API_TO_UPDATE_USER_STATISTIC');
    yield put({
      type: 'UPDATE_USER_STATISTIC',
      payload: action.payload,
    });
  } catch (error) {
    console.log(error); //eslint-disable-line
  }
}

function* updateRowDeviationType(action) {
  try {
    const { id, deviationType } = action.payload;
    const { timesheetStore: { monthDays } } = yield select();
    const url = `${baseUrl}/onholiday`;
    let postBody;

    if (deviationType === -1) {
      postBody = {
        day: monthDays[id].date,
        value: '',
        state: false,
        deleteTime: true,
      };
    } else {
      postBody = {
        day: monthDays[id].date,
        value: getDeviationTitle(deviationType),
        state: true,
        deleteTime: false,
      };
    }
    postData(url, postBody);
    // console.log('REQUEST_API_TO_UPDATE_ROW_DEVIATION_TYPE');
    yield put({
      type: 'UPDATE_ROW_DEVIATION_TYPE',
      payload: action.payload,
    });
  } catch (error) {
    console.log(error); //eslint-disable-line
  }
}

export default function* rootSaga() {
  yield takeEvery('ALL_DATA_REQUESTED', getAllData);
  yield takeEvery('SET_USERDATA_REQUESTED', postUserData);
  yield takeEvery('UPDATE_USER_STATISTIC_REQUESTED', updateUserStatistic);
  // Row data
  yield takeEvery('UPDATE_ROW_TIME_REQUESTED', updateRowTime);
  yield takeEvery('UPDATE_ROW_DEVIATION_TYPE_REQUESTED', updateRowDeviationType);
}

/**
 * chained promise function, which responsible for sending a GET request to the provided url,
 * then parse the response into json
 * @param {string} url - the url of the API endpoint needed to be called
 * @returns {object} response from the url
 */
export function getData(url) {
  return fetch(url, {
    headers: {
      'Content-Type': 'application/json',
    },
  })
    .then(response => response.json())
    .catch((error) => {
      throw error;
    });
}
/**
 * chained promise function, which responsible for sending a POST request to the provided url,
 * then parse the response to json
 * @param {string} url - the url of the API endpoint needed to be called
 * @param {object} data - the data, that must be sent in the query's body
 */
export function postData(url, data) {
  return fetch(url, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
  })
    .then(response => response.json())
    .catch((error) => {
      throw error;
    });
}

import { createStore, compose, applyMiddleware } from 'redux';
import createSagaMiddleware from 'redux-saga';
// eslint-disable-next-line import/no-extraneous-dependencies
import reduxImmutableStateInvariant from 'redux-immutable-state-invariant';
import rootReducer from '../reducers/rootReducer';
import rootSaga from '../sagas/sagas';

function configureStore(initialState) {
  const sagaMiddleware = createSagaMiddleware();
  const middlewares = [
    reduxImmutableStateInvariant(),
    sagaMiddleware,
  ];

  // eslint-disable-next-line no-underscore-dangle, max-len
  const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose; // add support for Redux dev tools
  const store = createStore(
    rootReducer,
    initialState,
    composeEnhancers(applyMiddleware(...middlewares)),
  );

  sagaMiddleware.run(rootSaga);

  return store;
}

export default configureStore;

import moment from 'moment';
/**
 * turns php timestamp to js timestamp
 * @param {number} timestamp - 10 digit php timestamp
 * @returns {number} js timestamp
 */
export const timestampToDate = timestamp => new Date(timestamp * 1000);
/**
 * turns js timestamp to php timestamp
 * @param {number} timestamp - js timestamp
 * @returns {number} php timestamp
 */
export const JsTimestampToPhpTimestamp = timestamp => new Date(timestamp / 1000);
/**
 * accepts a date string and converts it to php timestamp
 * @param {string} timestamp - date string in YY.MM.DD format
 * @returns {number} php timestamp
 */
export const stringToPhpTimeStamp = (timestamp) => {
  const jsTs = new Date(timestamp).getTime();
  return jsTs / 1000;
};
/**
 * Converts php timestamp to YY.MM.DD string format
 * @param {number} phpTs - 10 digit php timestamp
 * @returns {string} YY.MM.DD date format
 */
export const formatPhpTimestamp = (phpTs) => {
  if (!phpTs) { return ''; }
  const jsTs = timestampToDate(phpTs);
  const year = jsTs.getFullYear();
  const month = jsTs.getMonth() + 1 < 10 ? `0${jsTs.getMonth() + 1}` : jsTs.getMonth() + 1;
  const day = jsTs.getDate() < 10 ? `0${jsTs.getDate()}` : jsTs.getDate();

  return `${year}.${month}.${day}`;
};

export const dateToString = (timestamp) => {
  const jsTs = timestampToDate(timestamp);
  const year = jsTs.getFullYear();
  const month = jsTs.getMonth() + 1 < 10 ? `0${jsTs.getMonth() + 1}` : jsTs.getMonth() + 1;
  const day = jsTs.getDate() < 10 ? `0${jsTs.getDate()}` : jsTs.getDate();

  return `${year}.${month}.${day}`;
};
/**
 * converts HH:MM time format to minutes
 * @param {string} stringClock - HH:MM time format
 * @returns {number} input time in minutes
 */
export const stringClockToMin = (stringClock) => {
  const input = stringClock;
  const [hour, min] = input.split(':');
  const result = parseInt(hour, 10) * 60 + parseInt(min, 10);
  return result;
};

export const getMonthName = (monthIndex) => {
  moment.locale('hu');
  return moment.months(monthIndex);
};

export const isCurrentDay = (timestamp) => {
  const date = new Date();

  const year = date.getFullYear();
  const month = date.getMonth() + 1 < 10 ? `0${date.getMonth() + 1}` : date.getMonth() + 1;
  const day = date.getDate() < 10 ? `0${date.getDate()}` : date.getDate();

  const dateNow = `${year}.${month}.${day}`;

  return dateNow === dateToString(timestamp);
};

export const getMonths = () => {
  const date = new Date();
  const year = date.getFullYear();
  const month = date.getMonth();

  return new Array(12)
    .fill()
    .map((item, index) => ({
      title: `${year}/${index + 1}`,
      name: getMonthName(index),
      // eslint-disable-next-line no-unneeded-ternary
      actual: month === index ? true : false,
    }));
};
/**
 * converts a sum of minutes into HH:MM string format
 * @param {number} number - time in minutes
 * @returns {string} - time in HH:MM format
 */
export const minToHour = (number) => {
  if (number !== 0 && !number) {
    return '';
  }

  const formattedNumber = Math.abs(number);
  const hours = formattedNumber / 60;
  const rhours = Math.floor(hours);
  const minutes = (hours - rhours) * 60;
  const rminutes = Math.round(minutes) < 10 ? `0${Math.round(minutes)}` : Math.round(minutes);

  return `${rhours}:${rminutes}`;
};

export const getPlannedWorkTitle = (defaultDateTitle, defaultStart, defaultEnd) => {
  if (defaultDateTitle !== 'munkanap') {
    return defaultDateTitle;
  }
  return `${minToHour(defaultStart)}-${minToHour(defaultEnd)}`;
};

// eslint-disable-next-line consistent-return
export const decideSign = (worked) => {
  // console.log(worked);
  const sign = Math.sign(worked);

  if (sign === 0 || sign === 1) {
    return null;
  }

  if (sign === -1) {
    return '-';
  }
};

